import React, { useState } from "react";
import contacts from "./contacts";

function Filter() {
  const [search, setSearch] = useState("");
  function changed(e) {
    const userInput = e.target.value;
    setSearch(userInput);
  }
  function check(e) {
    e.preventDefault();
  }
  return (
    <div>
      <input
        type="text"
        onChange={changed}
        value={search}
        placeholder="enter contact name"
      />
      <button onClick={check}>Check</button>
      {contacts.filter((searched) => {
        return (
          <div>
            <p>{search.name}</p>
            <p>{search.villa}</p>
            <p>{search.tel}</p>
          </div>
        );
      })}
    </div>
  );
}

export default Filter;
