  const aray = [
    { name: "muka", age: 10, home: "lugonjo" },
    { name: "mac", age: 11, home: "kireka" },
    { name: "eric", age: 13, home: "banda" },
  ];
  const [inputText, setInputText] = useState("");
  const [results, setResults] = useState([]);
  function changed(e) {
    const newVal = e.target.value;
    setInputText(newVal);
  }
  function tried() {
    console.log(aray);
    const newAray = aray.filter((e) => {
      return e.name === inputText;
    });
    setResults(newAray);
    console.log(newAray);
  }

  return (
    <div>
      <input
        type="text"
        name=""
        onChange={changed}
        value={inputText}
        placeholder="check"
      />
      <h1 onClick={tried}>tried</h1>
      <p>
        {results.map((r) => {
          return <Try2 name={r.name} age={r.age} home={r.home} />;
        })}
      </p>
    </div>
  );
}

export default Try;
